import React, { useEffect } from 'react'
import './styles.css' ; 
import {fetchGlobalData , fetchCountries} from './api' 
import {Bar} from 'react-chartjs-2' ; 
const App = () => {
    useEffect(() => {
        fetchGlobalData() ; 
        fetchCountries() ; 
    } , []) ; 
    return (
        <React.Fragment>
            <Bar
            data = {{
                labels:['Total Cases' , 'Recorvered' , 'Deaths'] , 
                datasets:[
                    {
                        label:["Cases"], 
                        data:[2 , 1 , 1] , 
                        backgroundColor:['blue' , 'green' , 'red' ] ,
                    }
                ]
            }}
            />
        </React.Fragment>
    )
}

export default App
